import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "../assets/dashbord.css";

toast.configure();

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({ name: "", email: "", role: "", password: "" });
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          navigate("/login");
          return;
        }

        const res = await axios.get("http://localhost:8002/api/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (res.data.success) {
          setUser(res.data.user);
          setFormData({
            name: res.data.user.name || "",
            email: res.data.user.email || "",
            role: res.data.user.role || "",
            password: "",
          });
          if (res.data.user.profile) {
            setPreviewUrl(`http://localhost:8002/${res.data.user.profile}`);
          }
        }
      } catch (error) {
        console.error("❌ Error fetching user profile:", error.response?.data || error.message);
      }
    };

    fetchUser();
  }, [navigate]);

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedFile(file);
      setPreviewUrl(URL.createObjectURL(file));
    }
  };

  const handleUpdateProfile = async () => {
    try {
      const token = localStorage.getItem("token");
      const data = new FormData();
      data.append("name", formData.name);
      data.append("email", formData.email);
      data.append("role", formData.role);
      if (formData.password) data.append("password", formData.password);
      if (selectedFile) data.append("profile", selectedFile);

      const res = await axios.put("http://localhost:8002/api/profile-edit", data, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (res.data.success) {
        toast.success(res.data.message);
        setUser(res.data.user);
        setFormData({
          name: res.data.user.name,
          email: res.data.user.email,
          role: res.data.user.role,
          password: "",
        });
        if (res.data.user.profile) {
          setPreviewUrl(`http://localhost:8002/${res.data.user.profile}`);
        }
        setIsEditing(false);
      }
    } catch (error) {
      console.error("❌ Error updating profile:", error.response?.data || error.message);
      toast.error(error.response?.data?.message || "Update failed");
    }
  };

  if (!user) return <p>Loading user data...</p>;

  return (
    <div className="profile-container">
      <div className="profile-header">
        <h2>User Profile</h2>
        <button onClick={() => navigate("/dashboard")} className="back-button">
          Back to Dashboard
        </button>
      </div>

      <div className="profile-content">
        <div className="profile-image-section">
          <div className="profile-image-container">
            <img
              src={previewUrl || "http://localhost:8002/uploads/images/default-profile.png"}
              alt="Profile"
              className="profile-image"
            />
            <input type="file" accept="image/*" onChange={handleFileChange} />
          </div>
        </div>

        <div className="profile-details">
          <div className="profile-field">
            <label>Name:</label>
            <input type="text" name="name" value={formData.name} onChange={handleChange} disabled={!isEditing} />
          </div>
          <div className="profile-field">
            <label>Email:</label>
            <input type="email" name="email" value={formData.email} onChange={handleChange} disabled={!isEditing} />
          </div>
          <div className="profile-field">
            <label>Role:</label>
            <input type="text" name="role" value={formData.role} onChange={handleChange} disabled={!isEditing} />
          </div>
          <div className="profile-field">
            <label>Password:</label>
            <input type="password" name="password" value={formData.password} onChange={handleChange} disabled={!isEditing} />
          </div>

          {!isEditing ? (
            <button onClick={() => setIsEditing(true)} className="edit-button">
              Edit
            </button>
          ) : (
            <div className="action-buttons">
              <button onClick={handleUpdateProfile} className="save-button">
                Update
              </button>
              <button onClick={() => setIsEditing(false)} className="cancel-button">
                Cancel
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
