import '../assets/products.css';
import React, { useState, useEffect } from "react";

const Products = () => {
  const [products, setProducts] = useState([]);

  const showProduct = async () => {
    const productApi = await fetch("https://dummyjson.com/products");
    const fetchData = await productApi.json();

    setProducts(fetchData.products);
  };

  useEffect(() => {
    showProduct();
  }, []);

  return (
      <div className="item-main-outer">
        {products.map((item, i) => (
            <div key={i} className="item-main-inner">
            <img
              className="item-thumbnail-images"
              src={item.thumbnail}
              alt={item.title}
            />
            <h2 className="item-title">{item.title}</h2>
            <p className="item-rating">Rating: {item.rating}</p>
            <p className="item-price">${item.price}</p>
            <p className="item-tags">Tags: {item.tags?.join(", ")}</p>
            <p className="item-weight">Weight: {item.weight || "N/A"}</p>
            <p className="item-description">{item.description}</p>
            <p className="item-availabilityStatus">
              {item.availabilityStatus || "In Stock"}
            </p>
            <p className="item-warrantyInformation">
              {item.warrantyInformation || "No warranty info"}
            </p>
            <div className="flex justify-center">
              <img
                className="qe-code"
                src={item.qrCode || "https://cdn.dummyjson.com/public/qr-code.png"}
                alt={item.title}
              />
            </div>
          </div>
        ))}
      </div>
  );
};

export default Products;


