import '../assets/products.css';
import React, { useState, useEffect } from "react";

const Products = () => {
  const [products, setProducts] = useState([]);

  const showProduct = async () => {
    const productApi = await fetch("https://dummyjson.com/products");
    const fetchData = await productApi.json();

    // Add dummy qrCode manually to each product
    const updatedProducts = fetchData.products.map((item) => ({
      ...item,
      qrCode: "https://cdn.dummyjson.com/public/qr-code.png",
    }));

    setProducts(updatedProducts);
  };

  useEffect(() => {
    showProduct();
  }, []);

  return (
    <div className="p-4">
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {products.map((item, i) => (
          <div key={i} className="border p-4 rounded-xl shadow hover:shadow-lg transition">
            <img
              className="w-full h-48 object-cover rounded mb-4"
              src={item.thumbnail}
              alt={item.title}
            />
            <h2 className="text-lg font-semibold mb-1">{item.title}</h2>
            <p className="text-green-600 font-bold mb-1">${item.price}</p>
            <p className="text-sm text-gray-600 mb-1">Tags: {item.tags?.join(", ")}</p>
            <p className="text-sm text-gray-600 mb-1">Weight: {item.weight || 'N/A'}</p>
            <p className="text-sm text-yellow-600 mb-1">Rating: {item.rating}</p>
            <p className="text-sm text-gray-800 mb-2">{item.description}</p>
            <p className="text-sm text-blue-500 mb-1">{item.availabilityStatus || 'In Stock'}</p>
            <p className="text-sm text-purple-600 mb-3">{item.warrantyInformation || 'No warranty info'}</p>
            <div className="flex justify-center">
              <img
                className="w-24 h-24 object-contain"
                src={item.qrCode}
                alt={`${item.title} QR`}
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Products;


