import React ,{ useState, useEffect } from "react";

const Products=()=>{
const [products, setProducts]= useState([]);
const showProduct =async()=>{
  const productApi = await fetch('https://dummyjson.com/products');
  const fetchData = await productApi.json();
  setProducts(fetchData.products);

}
useEffect(()=>{
  showProduct();
},[])
return(
  <div className="main-product-outer">
{products.map((item,i)=>(
  <div>
  <h2>{item.title}</h2>
  <p>${item.price}</p>
<div>{item.tags,','}</div>
  <div className="product-decription">{item.description}</div>
   </div>
))}

  </div>
)
}

export default Products;

