// src/components/Users.js
import React, { useState, useEffect } from "react";
import '../assets/users.css';

const Users = () => {
  const [users, setUsers] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editData, setEditData] = useState({ name: '', email: '', role: '', gender: '', _id: '', profileFile: null });
  const [previewUrl, setPreviewUrl] = useState('');

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await fetch(`http://localhost:8002/api/view-user`);
      const data = await response.json();
      setUsers(data.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const openEditForm = (user) => {
    setEditData({
      _id: user._id,
      name: user.name,
      email: user.email,
      role: user.role,
      gender: user.gender || '',
      profileFile: null,
    });
    setPreviewUrl(user.profile ? `http://localhost:8002/${user.profile}` : '');
    setIsEditing(true);
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setEditData(prev => ({ ...prev, profileFile: file }));
      setPreviewUrl(URL.createObjectURL(file));
    }
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('name', editData.name);
      formData.append('email', editData.email);
      formData.append('role', editData.role);
      formData.append('gender', editData.gender);
      if (editData.profileFile) formData.append('profile', editData.profileFile);

      const response = await fetch(`http://localhost:8002/api/update-user/${editData._id}`, {
        method: "PUT",
        body: formData,
      });

      const result = await response.json();

      if (response.ok) {
        // Update local state
        setUsers(prev => prev.map(u =>
          u._id === editData._id
            ? {
                ...u,
                name: editData.name,
                email: editData.email,
                role: editData.role,
                gender: editData.gender,
                profile: result.user.profile || u.profile
              }
            : u
        ));
        setIsEditing(false);
      } else {
        console.error("Failed to update user:", result.message);
      }
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  return (
    <div className="users-container">
      <h2 className="table-title">User List</h2>
      <div className="table-wrapper">
        <table className="user-table">
          <thead>
            <tr>
              <th>Id</th>
              <th>Name</th>
              <th>Email</th>
              <th>Profile</th>
              <th>Gender</th>
              <th>Role</th>
              <th>Verified</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user, index) => (
              <tr key={user._id}>
                <td>{index + 1}</td>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>
                  <img
                    src={user?.profile ? `http://localhost:8002/${user.profile}` : "http://localhost:8002/uploads/images/Signup-1741170871891.png"}
                    alt={user.name}
                    className="profile-image"
                  />
                </td>
                <td>{user.gender}</td>
                <td>{user.role}</td>
                <td>{user.verified ? "✅ Verified" : "❌ Not Verified"}</td>
                <td>
                  <button onClick={() => openEditForm(user)}>Edit</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Edit Modal */}
      {isEditing && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>Edit User</h3>
            <form onSubmit={handleEditSubmit}>
              <label>
                Name:
                <input
                  type="text"
                  value={editData.name}
                  onChange={(e) => setEditData(prev => ({ ...prev, name: e.target.value }))}
                  required
                />
              </label>
              <label>
                Email:
                <input
                  type="email"
                  value={editData.email}
                  onChange={(e) => setEditData(prev => ({ ...prev, email: e.target.value }))}
                  required
                />
              </label>
              <label>
                Role:
                <select
                  value={editData.role}
                  onChange={(e) => setEditData(prev => ({ ...prev, role: e.target.value }))}
                  required
                >
                  <option value="user">User</option>
                  <option value="admin">Admin</option>
                </select>
              </label>
              <label>
                Gender:
                <select
                  value={editData.gender}
                  onChange={(e) => setEditData(prev => ({ ...prev, gender: e.target.value }))}
                  required
                >
                  <option value="">Select Gender</option>
                  <option value="Male">Male</option>
                  <option value="Female">Female</option>
                  <option value="Other">Other</option>
                </select>
              </label>
              <label>
                Profile Image:
                <input type="file" accept="image/*" onChange={handleFileChange} />
              </label>
              {previewUrl && <img src={previewUrl} alt="Preview" className="preview-image" />}
              <div className="modal-actions">
                <button type="submit">Save</button>
                <button type="button" onClick={() => setIsEditing(false)}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Users;
