import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../assets/dashbord.css';

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      if (!id) return;
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`http://localhost:8002/api/profile/${id}`, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        const profileData = res.data;
        setUser(profileData);

        if (profileData.profile) {
          setPreviewUrl(`http://localhost:8002/${profileData.profile}`);
        }
      } catch (error) {
        console.error('Error fetching user profile:', error);
      }
    };
    fetchUser();
  }, [id]);
  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      setPreviewUrl(URL.createObjectURL(file));
    }
  };
  const handleImageUpload = async () => {
    if (!selectedFile) return;
    const formData = new FormData();
    formData.append('profile', selectedFile);
    formData.append('userId', id);
    try {
      const token = localStorage.getItem('token');
      const res = await axios.put(
        'http://localhost:8002/api/profile',
        formData,
        {
          headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data',
          },
        }
      );
      if (res.data.success) {
        setUser({ ...user, profile: res.data.profile });
        setPreviewUrl(`http://localhost:8002/${res.data.profile}`);
        setIsEditing(false);
      }
    } catch (error) {
      console.error('Error uploading image:', error);
    }
  };
  if (!user) return <p>data not fetchData...</p>;
  return (
    <div className="profile-container">
      <div className="profile-header">
        <h2>User Profile</h2>
        <button onClick={() => navigate('/dashboard')} className="back-button">
          Back to Dashboard
        </button>
      </div>
      <div className="profile-content">
        <div className="profile-image-section">
          <div className="profile-image-container">
            <img
              src={
                previewUrl ||
                "http://localhost:8002/uploads/images/default-profile.png"
              }
              alt="Profile"
              className="profile-image"
            />
            {!isEditing ? (
              <button onClick={() => setIsEditing(true)} className="edit-button">
                Edit Profile Image
              </button>
            ) : (
              <div className="image-upload-controls">
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleFileChange}
                  className="file-input"
                />
                <button onClick={handleImageUpload} className="upload-button">
                  Upload
                </button>
                <button onClick={() => setIsEditing(false)} className="cancel-button">
                  Cancel
                </button>
              </div>
            )}
          </div>
        </div>
        <div className="profile-details">
          <div className="profile-field">
            <label>Name:</label>
            <p>{user.name}</p>
          </div>
          <div className="profile-field">
            <label>Email:</label>
            <p>{user.email}</p>
          </div>
          <div className="profile-field">
            <label>Verified:</label>
            <p>{user.isVerified ? 'Yes' : 'No'}</p>
          </div>
          <div className="profile-field">
            <label>Role:</label>
            <p>{user.role}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
