import React, { useMemo, useState } from "react";
import { ChevronLeft, ChevronRight, Calendar as CalendarIcon } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";

/**
 * Sleek, production-ready Calendar component
 * - Tailwind styling, rounded cards, soft shadows
 * - Month navigation + Today button
 * - Single-date selection
 * - Event dots per day + event list for the selected date
 * - Keyboard accessible
 *
 * Usage:
 * export default function App(){
 *   return <CalendarDemo />
 * }
 */

// ---------- Utility helpers (no external date libs) ----------
const toYMD = (d) => `${d.getFullYear()}-${String(d.getMonth()+1).padStart(2,"0")}-${String(d.getDate()).padStart(2,"0")}`;
const startOfMonth = (d) => new Date(d.getFullYear(), d.getMonth(), 1);
const endOfMonth = (d) => new Date(d.getFullYear(), d.getMonth() + 1, 0);
const addMonths = (d, n) => new Date(d.getFullYear(), d.getMonth() + n, 1);
const isSameDay = (a, b) => a && b && a.getFullYear() === b.getFullYear() && a.getMonth() === b.getMonth() && a.getDate() === b.getDate();
const isToday = (d) => isSameDay(d, new Date());

const WEEKDAYS = ["Sun","Mon","Tue","Wed","Thu","Fri","Sat"];

function buildCalendarGrid(viewDate){
  const first = startOfMonth(viewDate);
  const last = endOfMonth(viewDate);
  const startIndex = first.getDay(); // 0=Sun
  const daysInMonth = last.getDate();

  const cells = [];
  // Previous month trailing days
  const prevMonthLast = endOfMonth(addMonths(viewDate, -1)).getDate();
  for(let i = 0; i < startIndex; i++){
    const day = prevMonthLast - (startIndex - 1 - i);
    const d = new Date(viewDate.getFullYear(), viewDate.getMonth()-1, day);
    cells.push({ date: d, current: false });
  }
  // Current month
  for(let day = 1; day <= daysInMonth; day++){
    const d = new Date(viewDate.getFullYear(), viewDate.getMonth(), day);
    cells.push({ date: d, current: true });
  }
  // Next month leading days to fill 6 rows of 7 (42 cells)
  const total = 42;
  let nextDay = 1;
  while(cells.length < total){
    const d = new Date(viewDate.getFullYear(), viewDate.getMonth()+1, nextDay++);
    cells.push({ date: d, current: false });
  }
  return cells;
}

// ---------- Calendar Component ----------
function Calendar({
  value,
  onChange,
  events = [], // events: [{ date: 'YYYY-MM-DD', title: string, color?: string }]
  initialMonth = null,
  className = "",
}){
  const initial = initialMonth ? new Date(initialMonth.getFullYear(), initialMonth.getMonth(), 1) : new Date(new Date().getFullYear(), new Date().getMonth(), 1);
  const [viewDate, setViewDate] = useState(initial);

  const grid = useMemo(() => buildCalendarGrid(viewDate), [viewDate]);
  const monthLabel = viewDate.toLocaleString(undefined, { month: "long", year: "numeric" });

  // Map events by YMD for quick lookup
  const eventMap = useMemo(() => {
    const map = new Map();
    for(const e of events){
      const key = e.date;
      if(!map.has(key)) map.set(key, []);
      map.get(key).push(e);
    }
    return map;
  }, [events]);

  const handleKey = (e, date) => {
    if(e.key === 'Enter' || e.key === ' '){
      e.preventDefault();
      onChange?.(date);
    }
  };

  return (
    <div className={`w-full max-w-4xl mx-auto p-4 ${className}`}>
      <div className="rounded-2xl overflow-hidden shadow-xl bg-white/80 backdrop-blur supports-[backdrop-filter]:bg-white/60 border border-gray-100">
        {/* Header */}
        <div className="bg-gradient-to-r from-indigo-500 via-purple-500 to-fuchsia-500 p-6 text-white">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <div className="p-2 rounded-xl bg-white/20">
                <CalendarIcon className="w-6 h-6" />
              </div>
              <div>
                <div className="text-sm/5 opacity-90">Calendar</div>
                <div className="text-2xl font-semibold tracking-tight">{monthLabel}</div>
              </div>
            </div>
            <div className="flex items-center gap-2">
              <button
                className="inline-flex items-center justify-center rounded-xl px-3 py-2 bg-white/20 hover:bg-white/30 transition focus:outline-none focus:ring-2 focus:ring-white"
                onClick={() => setViewDate(d => addMonths(d, -1))}
                aria-label="Previous month"
              >
                <ChevronLeft className="w-5 h-5" />
              </button>
              <button
                className="inline-flex items-center justify-center rounded-xl px-3 py-2 bg-white/20 hover:bg-white/30 transition focus:outline-none focus:ring-2 focus:ring-white"
                onClick={() => setViewDate(d => addMonths(d, 1))}
                aria-label="Next month"
              >
                <ChevronRight className="w-5 h-5" />
              </button>
              <button
                className="ml-2 hidden sm:inline-flex items-center rounded-xl px-4 py-2 bg-white text-gray-900 hover:bg-gray-100 transition shadow"
                onClick={() => setViewDate(new Date(new Date().getFullYear(), new Date().getMonth(), 1))}
              >
                Today
              </button>
            </div>
          </div>
        </div>

        {/* Weekdays */}
        <div className="grid grid-cols-7 text-center text-xs font-medium tracking-wide uppercase text-gray-500 px-4 pt-4">
          {WEEKDAYS.map(d => (
            <div key={d} className="py-2 select-none">{d}</div>
          ))}
        </div>

        {/* Days grid */}
        <div className="px-4 pb-4">
          <AnimatePresence mode="popLayout" initial={false}>
            <motion.div
              key={monthLabel}
              initial={{ opacity: 0, y: 12 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -12 }}
              transition={{ duration: 0.22 }}
              className="grid grid-cols-7 gap-2"
            >
              {grid.map(({ date, current }, idx) => {
                const ymd = toYMD(date);
                const dayEvents = eventMap.get(ymd) || [];
                const selected = value && isSameDay(value, date);
                const today = isToday(date);

                return (
                  <button
                    key={`${ymd}-${idx}`}
                    onClick={() => onChange?.(date)}
                    onKeyDown={(e) => handleKey(e, date)}
                    className={[
                      "relative group aspect-square w-full rounded-2xl p-2 text-left focus:outline-none focus-visible:ring-2 focus-visible:ring-indigo-500 shadow-sm",
                      current ? "bg-white hover:bg-gray-50" : "bg-gray-50 hover:bg-gray-100 text-gray-400",
                      selected ? "ring-2 ring-indigo-500" : "",
                    ].join(" ")}
                    aria-pressed={selected}
                    aria-label={`Select ${date.toDateString()}`}
                  >
                    <div className="flex items-start justify-between">
                      <div className={[
                        "size-7 inline-flex items-center justify-center rounded-xl text-sm font-semibold",
                        today ? "bg-indigo-600 text-white" : "",
                      ].join(" ")}
                      >
                        {date.getDate()}
                      </div>
                      {selected && (
                        <span className="text-[10px] px-2 py-0.5 rounded-full bg-indigo-50 text-indigo-700 border border-indigo-200">Selected</span>
                      )}
                    </div>

                    {/* Event dots */}
                    {dayEvents.length > 0 && (
                      <div className="absolute inset-x-2 bottom-2 flex gap-1 overflow-hidden">
                        {dayEvents.slice(0, 4).map((e, i) => (
                          <span
                            key={i}
                            className="h-1.5 flex-1 rounded-full"
                            style={{ backgroundColor: e.color || "#6366F1" }}
                            title={e.title}
                          />
                        ))}
                        {dayEvents.length > 4 && (
                          <span className="text-[10px] ml-1 text-gray-500">+{dayEvents.length - 4}</span>
                        )}
                      </div>
                    )}
                  </button>
                );
              })}
            </motion.div>
          </AnimatePresence>
        </div>
      </div>
    </div>
  );
}

